import mongoose from"mongoose";import mongooseAggregatePaginate from"mongoose-aggregate-paginate-v2";const videoSchema=new mongoose.Schema({videoFile:{type:String,required:!0},thumbnail:{type:String,required:!0},title:{type:String,required:!0},description:{type:String,required:!0},duration:{type:Number,required:!0},views:{type:Number,default:0},isPublishes:{type:Boolean,default:!0},videoowner:{type:mongoose.Schema.Types.ObjectId,ref:"User",required:!0}},{timestamps:!0});videoSchema.plugin(mongooseAggregatePaginate);export const Video=mongoose.model("Video",videoSchema);